 (function($){$.fn.makeitchangefs=function(options){var defaults={defSize:12,incBy:1,upperLimit:14,lowerLimit:10};var options=$.extend(defaults,options);return this.each(function(){var ele=$(this);var fs=options.defSize;ele.css('font-size',fs);$('#resetfs').click(function(e){fs=options.defSize;ele.css('font-size',fs);e.preventDefault();});$('#plusfs').click(function(e){if(fs<options.upperLimit){fs=fs+options.incBy;ele.css('font-size',fs);e.preventDefault();}else{return false}});$('#minusfs').click(function(e){if(fs>options.lowerLimit){fs=fs-options.incBy;ele.css('font-size',fs);e.preventDefault();}else{return false}})})}})(jQuery);